function postwith(data) {
        var title = $('#promotion_node_title').val();
  	if (!title) { alert('Please enter the promotion title.'); return false; }

  	var body = $('#promotion_node_body').val();
  	if (!body) { alert('Please enter the promotion body.'); return false; }
  
  	var link = $('#promotion_node_link').val();
  	if (!link) { alert('Please enter the promotion link.'); return false; }
  
  	var photo = $('#promotion_node_photo_full_path').val();
  	var photo_file_name = photo.substr(photo.lastIndexOf('/')+1);
  	//alert("Photo Name: "+photo_file_name);
  	if(!photo) { alert('Please select a image from SD card or capture photo.'); return false;}

	var date = new Date();
	var timestamp = Math.round(date.getTime() / 1000);
	var node_object = {
		"type": "promotion", // your content type
		"title": title,
		"body": { "und": { 0 : { "value": encodeURIComponent(body) } } },
		"field_image": { "und": { 0 : { //be sure to change this to your image field name
			"fid": data.fid, //this is the fid we got back from services
			"alt": photo_file_name,
			"title": photo_file_name } } },
		"field_url": { "und": { 0 : { "value": encodeURIComponent(link) } } }
	};
	url = "http://bomo.b2k.hk/?q=my_services"; // Your services endpoint
	drupalServicesPostNode(node_object, url);
}
